import React, { useEffect } from 'react';

import { useLoaderData } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

import { dataActions } from './../redux/slice/dataSlice';

// Local Countries JSON Data
// import COUNTRIES_DATA from './../data.json';

import CountriesList from './../components/Countries/CountriesList';

function sortCountries(countries) {
	return countries.sort((countryA, countryB) => {
		if (countryA.name.common < countryB.name.common) {
			return -1;
		}
	});
}

export default function Countries() {
	const dispatch = useDispatch();

	const COUNTRIES_DATA = useLoaderData();
	dispatch(dataActions.setCountriesData({ data: COUNTRIES_DATA }));

	const searchQuery = useSelector((state) => state.searchQuery);
	const filterOption = useSelector((state) => state.filterOption);
	const countriesData = useSelector((state) => state.countriesData);

	console.log(countriesData);

	useEffect(() => {
		const filteredData = countriesData.filter((country) => {
			if (filterOption === 'All') {
				if (
					country.name.common
						.toLowerCase()
						.startsWith(searchQuery.toLowerCase())
				) {
					return country;
				} else if (
					country.name.common
						.toLowerCase()
						.includes(searchQuery.toLowerCase())
				) {
					return country;
				}
			} else {
				if (
					country.name.common
						.toLowerCase()
						.startsWith(searchQuery.toLowerCase()) &&
					country.region === filterOption
				) {
					return country;
				} else if (
					country.name.common
						.toLowerCase()
						.includes(searchQuery.toLowerCase()) &&
					country.region === filterOption
				) {
					return country;
				}
			}
		});

		dispatch(
			dataActions.setCountriesData({ data: sortCountries(filteredData) })
		);

		// setCountriesData(sortCountries(filteredData));
	}, [searchQuery, filterOption, countriesData]);

	return <CountriesList COUNTRIES_DATA={COUNTRIES_DATA} />;
}

export async function loader() {
	const response = await fetch('https://restcountries.com/v3.1/all');
	const data = await response.json();
	return data;
}
