		// 				.toLowerCase()
		// 				.includes(searchQuery.toLowerCase())
		// 		) {
		// 			return country;
		// 		}
		// 	} else {
		// 		if (
		// 			country.name.common
		// 				.toLowerCase()
		// 				.startsWith(searchQuery.toLowerCase()) &&
		// 			country.region === filterOption
		// 		) {
		// 			return country;
		// 		} else if (
		// 			country.name.common
		// 				.toLowerCase()
		// 				.includes(searchQuery.toLowerCase()) &&
		// 			country.region === filterOption
		// 		) {
		// 			return country;
		// 		}
		// 	}

		// });

		const filteredData = countriesData.filter((country) => {
			if (country.name.common.startsWith('A')) {
				return country;
			}
		});

		console.log(filteredData);

		dispatch(
			dataActions.setCountriesData({ data: sortCountries(filteredData) })
		);

		return;
	}, [searchQuery, filterOption, countriesData, dispatch, sortCountries]);

	return <CountriesList COUNTRIES_DATA={countriesData} />;
}

export async function loader() {
	const response = await fetch('https://restcountries.com/v3.1/all');
	const data = await response.json();
	return data;
}
