import { createSlice } from '@reduxjs/toolkit';

const themeSlice = createSlice({
	name: 'theme',
	initialState: {
		isDarkMode: false,
	},
	reducers: {
		toggleDarkMode(state) {
			return !state.isDarkMode;
		},
	},
});

export const themeActions = themeSlice.actions;

export default themeSlice.reducer;
