import React from 'react';

import { useLoaderData } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

import { dataActions } from './../redux/slice/dataSlice';

// Local Countries JSON Data
// import COUNTRIES_DATA from './../data.json';

import CountriesList from './../components/Countries/CountriesList';

export default function Countries() {
	const dispatch = useDispatch();
	searchQuery, filterOption, COUNTRIES_DATA;
	const searchQuery = useSelector((state) => state.searchQuery);
	const filterOption = useSelector((state) => state.filterOption);
	const countriesData = useSelector((state) => state.countriesData);

	useEffect(() => {
		
		const filteredData = COUNTRIES_DATA.filter((country) => {
			if (filterOption === 'All') {
				if (
					country.name.common
						.toLowerCase()
						.startsWith(searchQuery.toLowerCase())
				) {
					return country;
				} else if (
					country.name.common
						.toLowerCase()
						.includes(searchQuery.toLowerCase())
				) {
					return country;
				}
			} else {
				if (
					country.name.common
						.toLowerCase()
						.startsWith(searchQuery.toLowerCase()) &&
					country.region === filterOption
				) {
					return country;
				} else if (
					country.name.common
						.toLowerCase()
						.includes(searchQuery.toLowerCase()) &&
					country.region === filterOption
				) {
					return country;
				}
			}
		});
		setCountriesData(sortCountries(filteredData));
	}, [searchQuery, filterOption, COUNTRIES_DATA]);

	

	return <CountriesList COUNTRIES_DATA={COUNTRIES_DATA} />;
}

export async function loader() {
	const response = await fetch('https://restcountries.com/v3.1/all');
	const data = await response.json();
	return data;
}
