import React from 'react';

import { Link } from 'react-router-dom';

import CountryNavigation from './CountryNavigation';

export default function Country({ country }) {
	const countryDetails = {
		'Native Name': country.nativeName,
		Population: country.population,
		Region: country.region,
	};

	return (
		<>
			<CountryNavigation />
			<section className=" grid gap-20 grid-cols-1 md:grid-cols-2 flex-grow text-text">
				<div className="h-full w-full">
					<div className="w-full">
						<img
							className="w-full"
							src={country.flag}
							alt={`${country.name}'s flag`}
						/>
					</div>
				</div>
				<div className=" h-full w-full">
					<div>
						<h1>Philippines</h1>
					</div>
					<ul>
						{Object.entries(countryDetails).map((details) => {
							console.log(detail)
							// return <li className="flex">
							// <span className="font-semibold mr-1">Native Name: </span>
							// <p>
						})}
						
					</ul>
					<div>
						<div>
							<h2>Border Countries:</h2>
						</div>
						<div>
							<Link to={`/country/${country.alpha3code}`}></Link>
						</div>
					</div>
				</div>
			</section>
		</>
	);
}
