import React from 'react';

import { Link } from 'react-router-dom';

import CountryNavigation from './CountryNavigation';

export default function Country({ country, COUNTRIES_DATA }) {
	const countryDetails = {
		'Native Name': country.nativeName,
		Population: country.population,
		Region: country.region,
		'Sub region': country.subregion,
		Capital: country.capital,
		'Top Level Domain': country.topLevelDomain,
		Currencies: country.currencies[0].code,
		Languages: country.languages
			.map((language) => {
				return language.name;
			})
			.join(', '),
	};

	if (country.borders) {
		const borderCountries = country.borders.map((countryCode) => {
			const borderCountry = COUNTRIES_DATA.find((country) => {
				if (country.alpha3Code === countryCode) {
					return country;
				}
			});

			return {
				name: borderCountry.name,
				alpha3Code: borderCountry.alpha3Code,
			};
			// return country;
		});
	}

	console.log(borderCountries);

	return (
		<>
			<CountryNavigation />
			<section className=" grid gap-20 grid-cols-1 md:grid-cols-2 flex-grow text-text">
				<div className="h-full w-full">
					<div className="w-full">
						<img
							className="w-full"
							src={country.flag}
							alt={`${country.name}'s flag`}
						/>
					</div>
				</div>
				<div className=" h-full w-full">
					<div>
						<h1>{country.name}</h1>
					</div>
					<ul>
						{Object.entries(countryDetails).map((detail) => {
							return (
								<li className="flex">
									<span className="font-semibold mr-1">{`${detail[0]}:`}</span>
									<p>{detail[1]}</p>
								</li>
							);
						})}
					</ul>
					<div className="flex">
						<div className="mr-3">
							<h2>Border Countries:</h2>
						</div>
						<div
							className={`grid grid-rows-1 grid-cols-${borderCountries.length} gap-5 flex-grow`}
						>
							{borderCountries.map((borderCountry) => {
								return (
									<Link
										className="bg-elements"
										to={`/countries/${borderCountry.alpha3Code}`}
									>
										{borderCountry.name}
									</Link>
								);
							})}
						</div>
					</div>
				</div>
			</section>
		</>
	);
}
